import React, {useState} from 'react'
import Country from './showCountry'

const Countries = (props) => {
    const [showCountry, setShownCountry] = useState();

    const showThisCountry = event => {
        console.log(event.target)
        const select = props.countriesToShow.filter(country =>
          country.name.includes(event.target.value)
        );
        setShownCountry(select[0]);
      };
    if (props.countriesToShow.length>11) {
        return(
            <div>
                Too many matches, specify another filter
            </div>
        )
    }
    if (showCountry !== undefined) {
        return(
            <Country country={showCountry}/>
        )

    } else if (1<props.countriesToShow.length && props.countriesToShow.length<11) { 
        return(
            <div>
                <ul>
                    {props.countriesToShow.map(country =>
                    
                    <li key={country.name.common}>
                        {country.name.common}
                        <form>
                            <button type="submit" onClick={showThisCountry} >show</button>
                        </form>
                    </li>
                        )}
                </ul>
            </div>
        )
    } else if (props.countriesToShow.length === 1) {
        const country = props.countriesToShow[0]
        return(
            <Country country={country}/>
        )
    }
}

export default Countries
